#include <bits/stdc++.h>
using namespace std;
typedef long long ll;
typedef pair<int, int> pii;

const int MAXN = 2e5+5;

int n, da[MAXN], db[MAXN], x, y;
vector<int> a[MAXN], b[MAXN];
int ans = 0;
map<pii, int> m;
bool s[MAXN];

void compdb(int v, int f = -1) {
    if (f == -1)
        db[v] = 0;
    else
        db[v] = db[f]+1;

    for (int u : b[v])
        if (u != f)
            compdb(u, v);
}

void dfs(int v, int f = -1) {
    if (f == -1)
        da[v] = 0;
    else
        da[v] = da[f] + 1;
    if (da[v] > db[v])
        return;
    ans = max(ans, db[v]);
    if (da[v] >= db[v])
        return;

    if (s[v]) {
        cout << "-1\n";
        exit(0);
    }

    for (int u : a[v])
        if (u != f)
            dfs(u, v);
}

int main() {
    ios_base::sync_with_stdio(0); cin.tie(0); cout.tie(0);

    cin >> n >> x >> y;
    x--; y--;
    for (int i = 0; i < n-1; ++i) {
        int u, v;
        cin >> u >> v;
        u--; v--;
        a[u].push_back(v);
        a[v].push_back(u);
    }

    for (int i = 0; i < n-1; ++i) {
        int u, v;
        cin >> u >> v;
        u--; v--;
        b[u].push_back(v);
        b[v].push_back(u);
        m[pii(u, v)] = m[pii(v, u)] = 1;
    }

    for (int i = 0; i < n; ++i) {
        for (int v : a[i]) {
            bool z = 1;
            if (b[i].size() > b[v].size())
                continue;
            if (m.count(pii(i, v)) > 0)
                z = 0;
            for (int u : b[i])
                if (m.count(pii(u, v)) > 0)
                    z = 0;
            s[i] |= z;
            s[v] |= z;
        }
    }

    compdb(y);
    dfs(x);

    cout << 2*ans << endl;
}

