using namespace std;
struct node{int to,next;};
int n,rt1,rt2;
struct Tree{
	node e[410100];
	int h[210010],tot,dep[201000],fa[200100][21];
	inline void add(int from,int to){e[++tot].next=h[from];e[tot].to=to;h[from]=tot;}
	inline void Build() {
		for(rint i=1;i<n;++i){
			int a,b;cin>>a>>b;
			add(a,b);add(b,a);
		}
	}
	inline void DFS(int now,int ffa) {
		dep[now]=dep[ffa]+1;fa[now][0]=ffa;
		for(rint i=h[now];i;i=e[i].next) {
			int to=e[i].to;if(to==ffa) continue;
			DFS(to,now);
		}
	}
	inline void Build_St(){
		for(rint i=1;i<=20;++i) for(rint j=1;j<=n;++j) fa[j][i]=fa[fa[j][i-1]][i-1];
	}
	inline int Lca(int x,int y){
		if(x==y) return x;
		if(dep[x]>dep[y]) swap(x,y);
		for(rint i=20;i>=0;--i) if(dep[fa[y][i]]>=dep[x]) y=fa[y][i];
		if(x==y) return x;
		for(rint i=20;i>=0;--i) if(fa[x][i]!=fa[y][i]) x=fa[x][i],y=fa[y][i];
		return fa[x][0];
	}
	inline int Get_dis(int x,int y) {
		int lca=Lca(x,y);
		return dep[x]-dep[lca]+dep[y]-dep[lca];
	}
}A,B;
int ans=0;
inline void Solve(int now,int ffa){
	ans=max(ans,B.dep[now]-1);
	for(rint i=A.h[now];i;i=A.e[i].next) {
		int to=A.e[i].to;if(to==ffa) continue;
		A.dep[to]=A.dep[now]+1;
		if(A.dep[to]<B.dep[to]) {
			Solve(to,now);
		}
		if(B.Get_dis(to,now)>=3) {
			cout<<"-1\n";exit(0);
		}
	}
}
int main() {
	cin>>n>>rt1>>rt2;
	A.Build();
	B.Build();
	B.DFS(rt2,0);
	B.Build_St();
	A.dep[rt1]=1;
	Solve(rt1,0);
	cout<<ans*2<<endl;
	return 0;
}

