#define SIZE 200005

using namespace std;

vector <int> A[SIZE];
vector <int> B[SIZE];
int dep[SIZE],par[SIZE];
int dp[SIZE];

void solve(int v,int p,int d)
{
	par[v]=p;
	dep[v]=d;
	for(int i=0;i<B[v].size();i++)
	{
		int to=B[v][i];
		if(to!=p) solve(to,v,d+1);
	}
}
bool good(int s,int t)
{
	if(dep[s]>dep[t]) swap(s,t);//dep[s]<=dep[t]
	if(s==t||par[t]==s||par[s]==par[t]) return false;
	if(par[t]!=-1&&par[par[t]]==s) return false;
	return true;
}
int main()
{
	int n,X,Y;
	scanf("%d %d %d",&n,&X,&Y);X--,Y--;
	for(int i=0;i<n-1;i++)
	{
		int a,b;
		scanf("%d %d",&a,&b);a--;b--;
		A[a].push_back(b);
		A[b].push_back(a);
	}
	for(int i=0;i<n-1;i++)
	{
		int a,b;
		scanf("%d %d",&a,&b);a--;b--;
		B[a].push_back(b);
		B[b].push_back(a);
	}
	memset(dp,-1,sizeof(dp));
	solve(Y,-1,0);
	dp[X]=0;
	queue <int> que;
	que.push(X);
	int ret=0;
	while(!que.empty())
	{
		int v=que.front();que.pop();
		ret=max(ret,dep[v]*2);
		for(int i=0;i<A[v].size();i++)
		{
			int to=A[v][i];
			if(dp[to]!=-1) continue;
			if(good(v,to))
			{
				puts("-1");
				return 0;
			}
			if(dp[v]+1<dep[to])
			{
				dp[to]=dp[v]+1;
				que.push(to);
			}
		}
	}
	printf("%d\n",ret);
	return 0;
}

