int fa[200005], dep[200005], ans;
std::vector<int> adj_b[200005], adj_r[200005];
void init(int u, int f = -1)
{
	fa[u] = f;
	for (int v : adj_b[u])
	{
		if (v != f)
		{
			dep[v] = dep[u] + 1;
			init(v, u);
		}
	}
}
inline bool chk(int u, int v)
{
	int rem = 2;
	while (u != v && rem--)
	{
		int &x = dep[u] < dep[v] ? v : u;
		x = fa[x];
	}
	return u == v;
}
void dfs(int u, int len = 0, int f = -1)
{
	if (dep[u] > ans)
		ans = dep[u];
	if (dep[u] <= len)
		return;
	for (int v : adj_r[u])
	{
		if (v != f)
		{
			if (!chk(u, v))
			{
				puts("-1");
				throw false;
			}
			dfs(v, len + 1, u);
		}
	}
}
