const int maxn=4e5+10;

int n,x,y,fa[maxn],dep[maxn];

struct Tree{
	int tote,FIR[maxn],TO[maxn],NEXT[maxn];
	bool free[maxn];

	void addedge(int u,int v)
	{
		TO[++tote]=v;
		NEXT[tote]=FIR[u];
		FIR[u]=tote;
	}

	void dfs(int u)
	{
//		printf("dfs %d : dep=%d\n",u,dep[u]);
		for (int p=FIR[u];p;p=NEXT[p])
		{
			int v=TO[p];
			if (fa[u]==v) continue;
			fa[v]=u;
			dep[v]=dep[u]+1;
			dfs(v);
		}
	}

	int solve(int u,int f,int dis)
	{
//		printf("u=%d\n",u);
		int Ans=dep[u];
		for (int p=FIR[u];p;p=NEXT[p])
		{
			int v=TO[p];
			if (v==f) continue;
			if (dis>=dep[v]) continue;
			bool flag=fa[v]==u||fa[u]==v||fa[fa[v]]==u||fa[fa[u]]==v||fa[u]==fa[v];
			if (!flag) return 1e8;
//			printf("solving %d : %d--->%d\n",u,u,v);
			Ans=max(Ans,solve(v,u,dis+1));
		}
		return Ans;
	}
}red,blue;

