void dfsinit(int id,int pre){
    if(id==0){
        dep[id] = 0;
        p[id][0] = 0;
    }else{
        dep[id] = dep[pre]+1;
        p[id][0] = pre;
    }
    for(auto x:h[id]){
        if(x!=pre){
            dfsinit(x,id);
        }
    }
}
void init(){
    dep.resize(n);
    p.resize(n);
    rep(i,n){
        p[i].resize(20);
    }
    dfsinit(0,-1);
    for(int k=1;k<20;k++){
        rep(i,n){
            p[i][k] = p[p[i][k-1]][k-1];
        }
    }
}
int lca(int a,int b){
    if(dep[a]>dep[b])swap(a,b);
    for(int k=0;k<20;k++){
        if((dep[b]-dep[a])>>k & 1){
            b = p[b][k];
        }
    }
    if(a==b)return a;
    for(int k=19;k>=0;k--){
        if(p[a][k]!=p[b][k]){
            a = p[a][k];
            b = p[b][k];
        }
    }
    return p[a][0];
}
int dist(int a,int b){
    int c = lca(a,b);
    return dep[a] + dep[b] - 2*dep[c];
}
int next(int x,int y){
    int z = lca(x,y);
    if(x==z){
        return p[y][0];
    }else if(y==z){
        int s = dep[x]-dep[y]-1;
        for(int k=0;k<20;k++){
            if((s)>>k & 1){
                x = p[x][k];
                s -= (1<<k);
            }
        }
        return x;
    }else{
        return p[y][0];
    }
}
bool flag = 0;
void dfs(int x,int y,int pre){
    if(ng[x]){
        flag = true;
        return;
    }
    for(auto z:g[x]){
        if(z==pre)continue;
        if(dist(y,z)<=1)continue;
        dfs(z,next(z,y),x);
    }
    return ;
}
int dfs2(int x,int y,int pre){
    int mx = 2*(dist(x,y));
    for(auto z:g[x]){
        if(z==pre)continue;
        if(dist(y,z)<=1)continue;
        mx = max(mx,2+dfs2(z,next(z,y),x));
    }
    return mx;
}
