int chk[N],u[N],v[N];
int n,x,y,ans;

struct Edge{
	int y,nxt;
};

struct Tree{
	Edge E[N*2];
	int fa[20][N];
	int las[N],dep[N];
	int cnt;
	Tree(){
		memset(las,-1,sizeof(las));
	}
	void Link(int x,int y){
		E[cnt]=(Edge){y,las[x]};las[x]=cnt++;
		E[cnt]=(Edge){x,las[y]};las[y]=cnt++;
	}
	void dfs(int x){
		for (int i=las[x],y;~i;i=E[i].nxt)
			if ((y=E[i].y)!=fa[0][x]){
				fa[0][y]=x;
				dep[y]=dep[x]+1;
				dfs(y);
			}
	}
	int LCA(int x,int y){
		if (dep[x]>dep[y]) swap(x,y);
		for (int k=dep[y]-dep[x],i=0;k;k>>=1,i++) if (k&1) y=fa[i][y];
		if (x==y) return x;
		for (int i=19;~i;i--) if (fa[i][x]!=fa[i][y]) x=fa[i][x],y=fa[i][y];
		return fa[0][x];
	}
	int dis(int x,int y){
		return dep[x]+dep[y]-2*dep[LCA(x,y)];
	}
	void Work(int rt){
		dfs(rt);
		For(i,1,20) For(x,1,n+1) fa[i][x]=fa[i-1][fa[i-1][x]];
	}
} R,B;

void Dfs(int x){
	chk[x]=max(chk[R.fa[0][x]],R.dep[x]-B.dep[x]);
	for (int i=R.las[x],y;~i;i=R.E[i].nxt)
		if ((y=R.E[i].y)!=R.fa[0][x]){
			Dfs(y);
		}
}

