  x--;y--;
  // G: sugim, H: sigma
  vector<vector<int> > G(n),H(n);
  for(int t=0;t<2;t++){
    auto &T=(t==0?H:G);
    for(int i=1;i<n;i++){
      int a,b;
      cin>>a>>b;
      a--;b--;
      T[a].emplace_back(b);
      T[b].emplace_back(a);
    }
  }

  LowestCommonAncestor lca(n);
  lca.G=G;
  lca.build(y);

  vector<int> dist(n,-1);
  queue<int> q;
  dist[x]=0;
  q.emplace(x);

  auto WIN=[](){cout<<-1<<endl;exit(0);};
  int ans=0;
  while(!q.empty()){
    int v=q.front();q.pop();
    chmax(ans,lca.dep[v]);
    for(int u:H[v]){
      if(~dist[u]) continue;
      dist[u]=dist[v]+1;
      if(dist[u]>=lca.dep[u]) continue;
      q.emplace(u);

      if(lca.distance(u,v)>=3) WIN();
    }
  }

  cout<<ans*2<<endl;
