const int N = 200001;

int n, x, y, a[N], b[N], depth[N], parent[N], escape[N], dist[N];
std::vector<int> tree[N], graph[N];

void prepare(int p, int u) {
  depth[u] = depth[p] + 1, parent[u] = p;
  for (int v : tree[u]) {
    if (v != p) {
      prepare(u, v);
    }
  }
}

int treedist(int a, int b) {
  int d = 0;
  while (d < 3 && a != b) {
    int &u = depth[a] > depth[b] ? a : b;
    d++, u = parent[u];
  }
  return d;
}

int main() {
  scanf("%d%d%d", &n, &x, &y);
  for (int i = 0; i < n - 1; ++i) {
    scanf("%d%d", a + i, b + i);
  }
  for (int i = 0, c, d; i < n - 1; ++i) {
    scanf("%d%d", &c, &d);
    tree[c].push_back(d);
    tree[d].push_back(c);
  }
  depth[0] = -1;
  prepare(0, y);
  for (int i = 0; i < n - 1; ++i) {
    if (treedist(a[i], b[i]) == 3) {
      escape[a[i]] = escape[b[i]] = true;
    } else {
      graph[a[i]].push_back(b[i]);
      graph[b[i]].push_back(a[i]);
    }
  }
  std::vector<int> queue;
  queue.push_back(x);
  memset(dist, -1, sizeof(dist));
  dist[x] = 0;
  for (int head = 0; head < static_cast<int>(queue.size()); ++head) {
    int u = queue[head];
    for (int v : graph[u]) {
      if (dist[v] == -1) {
        dist[v] = dist[v] + 1;
        queue.push_back(v);
      }
    }
  }
  int result = 0;
  for (int i = 1; i <= n; ++i) {
    if (~dist[i] && dist[i] < depth[i]) {
      result = std::max(result, depth[i] * 2);
    }
    if (escape[i] && ~dist[i] && dist[i] < depth[i]) {
      result = -1;
      break;
    }
  }
  printf("%d\n", result);
}

#include <bits/stdc++.h>
#define SZ(x) ((int) (x).size())
using namespace std;

typedef long long i64;

const int NMAX = 200005, LOG = 20;
const int INF = 0x3f3f3f3f;

vector<int> T1[NMAX], T2[NMAX];
int father[NMAX], level[NMAX];
int anc[LOG][NMAX];

int dist[NMAX];

void dfs(int node, int prev) {
    father[node] = prev;
    for (int to: T2[node]) {
        if (to != prev) {
            level[to] = level[node] + 1;
            dfs(to, node);
        }
    }
}

int goUp(int x, int lvl) {
    for (int k = LOG - 1; k >= 0; --k) {
        if (lvl & (1 << k)) {
            x = anc[k][x];
        }
    }
    return x;
}

int lca(int x, int y) {
    if (level[y] > level[x]) {
        y = goUp(y, level[y] - level[x]);
    } else if (level[x] > level[y]) {
        x = goUp(x, level[x] - level[y]);
    }
    if (x == y) {
        return x;
    }
    for (int k = LOG - 1; k >= 0; --k) {
        if (anc[k][x] != anc[k][y]) {
            x = anc[k][x];
            y = anc[k][y];
        }
    }
    return father[x];
}

int xdist(int x, int y) {
    return level[x] + level[y] - 2 * level[lca(x, y)];
}

