}
void lcainit(int num)
{
	for (int i = 1; i < LOG; i++)
	{
		for (int j = 0; j < num; j++)
		{
			par[i][j] = par[i - 1][par[i - 1][j]];
		}
	}
}
bool isanc(int a, int b)
{
	for (int i = 19; i >= 0; i--)if (dep[a] >= dep[b] + (1 << i))a = par[i][a];
	return a == b;
}
