{
	if (flag[node])return;
	flag[node] = true;
	if (t >= dep[node])return;
	if (iswin[node])maxi = 1000000000;
	maxi = max(maxi, dep[node]);
	for (int i = 0; i < np[node].size(); i++)calc(np[node][i], t + 1);
}
