	to[++e] = v, nxt[e] = st[u];
	st[u] = e;
}

int E, St[MAXN], To[MAXN<<1];
int Nxt[MAXN<<1];
inline void Add(int u, int v) {
	To[++E] = v, Nxt[E] = St[u];
	St[u] = E;
}

int n, dep[MAXN], rx, ry;
int fa[MAXN][20], ans;
bool vis[MAXN], flag[MAXN];

void dfs(int u) {
	for(int i = st[u]; i; i = nxt[i]) {
		int v = to[i];
		if(v == fa[u][0]) continue;
		dep[v] = dep[u]+1;
		fa[v][0] = u, dfs(v);
	}
}

inline int LCA(int u, int v) {
	if(dep[u] < dep[v]) swap(u, v);
	for(int i = 18; i >= 0; i--) 
		if(dep[fa[u][i]] >= dep[v]) 
			u = fa[u][i];
	if(v == u) return u;
	for(int i = 18; i >= 0; i--) 
		if(fa[u][i] ^ fa[v][i])
			u = fa[u][i], v = fa[v][i];
	return fa[u][0];
}

