const int MN=200000;
int N,A,B;
vector<int> Ga[MN],Gb[MN];
bool vis[MN];	//Ga
typedef pair<int,int> P;


const int MAX_V=MN;
const int LOGV=18;
int par[MAX_V][LOGV],depth[MAX_V];
void dfs(int v,int p){
	if(p<0) depth[v]=0;
	else depth[v]=depth[p]+1;
	par[v][0]=p;
	for(int u:Gb[v]){
		if(u!=p) dfs(u,v);
	}
}
void genlca(int N){
	dfs(0,-1);
	for(int i=1;i<LOGV;i++){
		rep(v,N){
			if(par[v][i-1]==-1) par[v][i]=-1;
			else par[v][i]=par[par[v][i-1]][i-1];
		}
	}
}
int lca(int u,int v){
	if(depth[u]<depth[v]){
		swap(u,v);
	}
	int d=depth[u]-depth[v];
	rep(i,LOGV){
		if((d>>i)&1) u=par[u][i];
	}
	if(u==v) return u;
	for(int i=LOGV-1;i>=0;i--){
		if(par[u][i]!=par[v][i]){
			u=par[u][i];
			v=par[v][i];
		}
	}
	return par[v][0];
}


int distB(int x,int y){
	int w=lca(x,y);
	return depth[x]+depth[y]-2*depth[w];
}
int up(int v,int d){
	rep(i,LOGV) if((d>>i)&1) v=par[v][i];
	return v;
}
int toclose(int x,int tar){
	assert(x!=tar);
	int w=lca(x,tar);
	if(w!=x) return par[x][0];
	int d=depth[tar]-depth[x];
	return up(tar,d-1);
}

int solve(){
	cin>>N>>A>>B;
	A--,B--;
	rep(i,N-1){
		int x,y;
		scanf("%d%d",&x,&y);
		x--,y--;
		Ga[x].pb(y);
		Ga[y].pb(x);
	}
	rep(i,N-1){
		int x,y;
		scanf("%d%d",&x,&y);
		x--,y--;
		Gb[x].pb(y);
		Gb[y].pb(x);
	}

	genlca(N);

	int d=-1;
	queue<P> que;
	que.push(P(-1,-1));
	que.push(P(A,B));
	vis[A]=1;
	int ans=0;
	while(!que.empty()){
		P p=que.front();que.pop();
		if(p.sc==-1){
			if(que.empty()) break;
			que.push(P(d+1,-1));
			d++;
//			show(d);
			continue;
		}
		int a=p.fs,b=p.sc;
//		printf("a=%d,b=%d\n",a,b);
		chmax(ans,(d+distB(a,b))*2);
		for(int na:Ga[a]) if(!vis[na]){
			if(distB(a,na)>=3){
				return -1;
			}
			vis[na]=1;
			if(b==na) continue;//?
			int nb=toclose(b,na);
			if(nb==na) continue;
			que.push(P(na,nb));
		}
	}
	return max(ans,d*2);
}
int main(){
	cout<<solve()<<endl;
}

