		const int x=nxi(),y=nxi();
		G::vs[x].push_back(y);
		G::vs[y].push_back(x);
	}
	G::dfs_sz(G::st_s);
	G::dfs_top(G::st_s);
	if(G::dfs_free(G::st_f,0,0)){
		puts("-1");
		return 0;
	}
	G::dfs_xdis(G::st_f,0,0);
