	lca.initLCA(N);

	vector<int> used(N, 0);
	vector<int> pos(N, -1);
	vector<int> dist(N, 0);
	used[X] = true;

	stack<int> sta;
	sta.push(X);
	pos[X] = Y;
	dist[X] = lca.solveLCA(X, Y);

	int ans = 0;
	ans = dist[X];

	bool good = false;
	while(!sta.empty()) {
		int cur = sta.top(); sta.pop();
		for(int i=0; i<black[cur].size(); i++) {
			int u = black[cur][i];
			if(used[u]) continue;
			if(pos[cur] == u) continue;

			used[u] = true;
			int prevWhite = pos[cur];
			int nextWhite = -1;
			int minim = N;
			for(int j=0; j<white[prevWhite].size(); j++) {
				int tmpWhite = white[prevWhite][j];
				int diff = lca.solveLCA(tmpWhite, u);
				if(diff < minim) {
					minim = diff;
					nextWhite = tmpWhite;
				}

			}
			pos[u] = nextWhite;
			dist[u] = minim;
			//cout << u << " " << dist[u] << endl;
