				ans = max(ans, lca.solveLCA(u, Y));
			}
			if(dist[u]==0)
				ans = max(ans, lca.solveLCA(u, Y));
		}
	}
	if(good) {
		cout << "-1" << endl;
	}
	else {
		cout << ans * 2 << endl;
	}


	return 0;
}

