using namespace std;

const int MN = 200010;

int N, X, Y;
vector<int> adj1[MN], adj2[MN];
int par[20][MN], dep[MN], tin[MN], tout[MN], timer;

void dfs0(int u, int p) {
    tin[u] = timer++;

    par[0][u] = p;
    for(int i = 1; i < 20; i++) {
        int t = par[i - 1][u];
        if(t == -1) break;
        par[i][u] = par[i - 1][t];
    }

    for(int i = 0; i < adj2[u].size(); i++) {
        int v = adj2[u][i];
        if(v == p) continue;
        dep[v] = dep[u] + 1;
        dfs0(v, u);
    }

    tout[u] = timer;
}

int lca(int a, int b) {
    if(dep[a] < dep[b]) swap(a, b);
    int diff = dep[a] - dep[b];
    for(int i = 0; i < 20; i++) {
        if(diff & (1 << i)) a = par[i][a];
    }
    if(a == b) return a;
    for(int i = 20; i--;) {
        if(par[i][a] != par[i][b]) {
            a = par[i][a];
            b = par[i][b];
        }
    }
    return par[0][a];
}

int dist(int u, int v) {
    return dep[u] + dep[v] - 2 * dep[ lca(u, v) ];
}

int ans;
void dfs1(int u, int p) {
    ans = max(ans, dep[u]);
    for(int i = 0; i < adj1[u].size(); i++) {
        int v = adj1[u][i];
        if(v == p) continue;
        if(dist(v, u) >= 3) {
            printf("-1");
            exit(0);
        }
        if(dep[v] - dep[Y] < 2 && tin[Y] <= tin[v] && tin[v] < tout[Y]) continue;
        dfs1(v, u);
    }
}

int main() {
    scanf("%d %d %d", &N, &X, &Y);
    X--; Y--;

    for(int i = 0; i < N - 1; i++) {
        int a, b; scanf("%d %d", &a, &b);
        a--; b--;

        adj1[a].push_back(b);
        adj1[b].push_back(a);
    }
    for(int i = 0; i < N - 1; i++) {
        int a, b; scanf("%d %d", &a, &b);
        a--; b--;

        adj2[a].push_back(b);
        adj2[b].push_back(a);
    }

    dfs0(Y, -1);

    while(dep[X] - dep[Y] > 2) {
        int mn = 1e9, p = -1;
        for(int i = 0; i < adj1[X].size(); i++) {
            int v = adj1[X][i];

            if(dist(X, v) >= 3) {
                printf("-1");
                return 0;
            }

            if(mn > dep[v]) {
                mn = dep[v];
                p = v;
            }
        }
        if(mn >= dep[X]) break;
        X = p;

        for(int i = 0; i < adj2[Y].size(); i++) {
            int v = adj2[Y][i];
            if(tin[v] <= tin[X] && tin[X] < tout[v]) {
                Y = v;
                break;
            }
        }
    }

    dfs1(X, -1);

    printf("%d", 2 * ans);
}

