struct HeavyLightDecomposition{
  int root;
  // tree representation
  int N;              // size
  vi parent;          // parent[p] := parent of node p
  vi depth;           // depth[p] := depth of node p
  vi subtreesize;     // subtreesize[p] := size of subtree rooted at node p

  // heavy light decomposition
  vector<vi> paths;   // paths[i] := heavy edge path (id=i)
  vi belongs;         // belongs[p] := id of path that node p belongs to
  vi pathpos;         // pathpos[p] := index of node p in paths[belongs[p]] (paths[belongs[p]][pathpos[p]] == p) (for segment tree)
  vi heavyedge;       // heavyedge[p] := edge (p,heavyedge[p]) is heavy edge

  // tree construct dfs
  int treedfs(int p,int d,int bef,vi g[]){
    parent[p] = bef;
    depth[p] = d;
    subtreesize[p] = 1;
    int maxchild = -1;
    REP(i,g[p].size()){
      int to = g[p][i];
      if(to==bef)continue;
      subtreesize[p] += treedfs(to,d+1,p,g);
      if(maxchild == -1 || subtreesize[maxchild] < subtreesize[to]){
        maxchild = to;
      }
    }
    if(maxchild != -1 && subtreesize[maxchild]*2 >= subtreesize[p]){
      heavyedge[p] = maxchild;
    }
    return subtreesize[p];
  }

  // heavy light decomposition dfs
  void decompositiondfs(int pathid,int head,int bef,vi g[]){
    paths.push_back(vi());
    int cur = head;
    while(true){
      belongs[cur] = pathid;
      pathpos[cur] = paths[pathid].size();
      paths[pathid].push_back(cur);
      int nxt = heavyedge[cur];
      REP(i,g[cur].size()){
        int to = g[cur][i];
        if(to==bef)continue;
        if(to==nxt)continue;
        decompositiondfs(paths.size(),to,cur,g);
      }
      if(nxt==-1)break;
      bef = cur;
      cur = nxt;
    }
  }

  HeavyLightDecomposition(int n,vi g[],int _root){
    N = n;
    root = _root;
    parent.assign(n,-1);
    depth.assign(n,-1);
    subtreesize.assign(n,-1);
    belongs.assign(n,-1);
    pathpos.assign(n,-1);
    heavyedge.assign(n,-1);

    treedfs(root,0,-1,g);
    decompositiondfs(0,root,-1,g);
  }

  int lca(int a,int b){
    int ato = paths[belongs[a]][0];
    int bto = paths[belongs[b]][0];
    while(belongs[a]!=belongs[b]){
      if(depth[ato] > depth[bto]){
        a = ato;
        if(a!=root){
          a = parent[a];
          ato = paths[belongs[a]][0];
        }
      }else{
        b = bto;
        if(b!=root){
          b = parent[b];
          bto = paths[belongs[b]][0];
        }
      }
    }
    if(depth[a]<=depth[b]){
      return a;
    }else{
      return b;
    }
  }

  int length(int a,int b){
    return depth[a]+depth[b]-2*depth[lca(a,b)];
  }
};

