		y=pnt[i];
		if (y!=fa[x] && y!=son[x]) dvd(y,y);
	}
}
int lca(int x,int y){
	for (; anc[x]!=anc[y]; x=fa[anc[x]])
		if (dep[anc[x]]<dep[anc[y]]) swap(x,y);
	return dep[x]<dep[y]?x:y;
}
int dist(int x,int y){
	return dep[x]+dep[y]-(dep[lca(x,y)]<<1);
}
