        if (v == fx[u] || dx[v] >= dy[v]) continue;
        fx[v] = u, dx[v] = dx[u] + 1;
        if (dy[v] + dy[u] - 2 * dy[lca(u, v)] > 2) ans = -1;
        dfsx(v);
    }
}
