inline int Dis(int x, int y)
{
	int ret = 0;
	if (dep[x] < dep[y])
		swap(x, y);
	while (ret < 3 && dep[x] > dep[y])
		ret ++, x = par[x];
	while (ret < 3 && (x ^ y))
		ret += 2, x = par[x], y = par[y];
	return min(ret, 3);
}

