#include <bits/stdc++.h>
#pragma comment(linker, "/STACK:16000000")
using namespace std;

const int Maxn = 200005;
const int Maxm = 20;

int n, x, y;
vector <int> neigh1[Maxn], neigh2[Maxn];
int L[Maxn], P[Maxn][Maxm];
int L2[Maxn];

void Traverse(int v)
{
    for (int i = 0; i < neigh2[v].size(); i++) {
        int u = neigh2[v][i];
        if (u == P[v][0]) continue;
        P[u][0] = v; L[u] = L[v] + 1;
        Traverse(u);
    }
}

void Traverse2(int v, int p = 0)
{
    for (int i = 0; i < neigh1[v].size(); i++) {
        int u = neigh1[v][i];
        if (u == p) continue;
        if (L2[v] + 1 < L[u]) {
            L2[u] = L2[v] + 1;
            Traverse2(u, v);
        }
    }
}

int getLCA(int a, int b)
{
    if (L[a] < L[b]) swap(a, b);
    for (int i = Maxm - 1; i >= 0; i--)
        if (L[a] - (1 << i) >= L[b])
            a = P[a][i];
    if (a == b) return a;
    for (int i = Maxm - 1; i >= 0; i--)
        if (P[a][i] != P[b][i])
            a = P[a][i], b = P[b][i];
    return P[a][0];
}

int getDist(int a, int b) { return L[a] + L[b] - 2 * L[getLCA(a, b)]; }

int main()
{
    scanf("%d %d %d", &n, &x, &y);
    for (int i = 0; i < n - 1; i++) {
        int a, b; scanf("%d %d", &a, &b);
        neigh1[a].push_back(b);
        neigh1[b].push_back(a);
    }
    for (int i = 0; i < n - 1; i++) {
        int a, b; scanf("%d %d", &a, &b);
        neigh2[a].push_back(b);
        neigh2[b].push_back(a);
    }
    Traverse(y);
    for (int j = 1; j < Maxm; j++)
        for (int i = 1; i <= n; i++)
            P[i][j] = P[P[i][j - 1]][j - 1];
    fill(L2, L2 + Maxn, Maxn);
    L2[x] = 0;
    Traverse2(x);
    int res = 0;
    for (int i = 1; i <= n; i++)
        if (L2[i] < L[i]) {
            res = max(res, 2 * L[i]);
            for (int j = 0; j < neigh1[i].size(); j++) {
                int u = neigh1[i][j];
                if (getDist(i, u) > 2) { printf("-1\n"); return 0; }
            }
        }
    printf("%d\n", res);
    return 0;
}

