using namespace std;
const int N=200005;
struct edge{
	int to,next;
};
int n,res;
struct Tree{
	int fa[N],dep[N];
	edge e[N*2];
	int head[N],tot,rt;
	void add(int x,int y){
		e[++tot]=(edge){y,head[x]};
		head[x]=tot;
	}
	void dfs(int x){
		for (int i=head[x];i;i=e[i].next)
			if (e[i].to!=fa[x]){
				fa[e[i].to]=x;
				dep[e[i].to]=dep[x]+1;
				dfs(e[i].to);
			}
	}
	void init(int n){
		memset(head,0,sizeof(head));
		tot=dep[rt]=fa[rt]=0;
		for (int i=1;i<n;i++){
			int x,y;
			scanf("%d%d",&x,&y);
			add(x,y); add(y,x);
		}
		dfs(rt);
	}
	int dis(int x,int y){
		int ans=0;
		for (;x!=y&&ans<3;ans++)
			if (dep[x]>dep[y])
				x=fa[x];
			else y=fa[y];
		return ans==3;
	}
}A,B;
int dfs(int x){
	int ans=B.dep[x];
	for (int i=A.head[x];i;i=A.e[i].next){
		int to=A.e[i].to;
		if (to==A.fa[x]) continue;
		if (B.dis(x,to)) return 2e9;
		if (A.dep[to]<B.dep[to])
			ans=max(ans,dfs(to));
	}
	return ans;
}
int main(){
	scanf("%d%d%d",&n,&A.rt,&B.rt);
	A.init(n); B.init(n);
	int ans=dfs(A.rt);
	printf("%d\n",ans>1e9?-1:ans*2);
}

