{
	l[x] = ++k;
	for (int y : e_b[x])
	{
		if(y != fa)
		{
			dep[y] = dep[x] + 1;
			par[y] = x;
			dfs(y, x);
		}
	}
	r[x] = k;
}

bool cho[NX], good[NX]; int ans = 0;
