const int MAX_V = 200011;
const int LOG_V = 19;
int depth[MAX_V];
int parent[LOG_V][MAX_V];

void build_lca(VI G[], int N, const int root=0) {
    depth[root] = 0;
    parent[0][root] = root;
    VI ord; ord.reserve(N);
    ord.push_back(root);
    REP (i_, N) {
	int v = ord[i_];
	EACH (e, G[v]) if (*e != parent[0][v]) {
	    ord.push_back(*e);
	    depth[*e] = depth[v] + 1;
	    parent[0][*e] = v;
	    // G[*e].erase(remove(G[*e].begin(), G[*e].end(), v), G[*e].end()); // remove parent edge
	}
    }
    REP (t, LOG_V-1) REP (v, N) parent[t+1][v] = parent[t][parent[t][v]];
}

int lca(int x, int y) {
    if (depth[x] < depth[y]) swap(x, y);
    REP (t, LOG_V) if ((depth[x] - depth[y]) & (1 << t)) x = parent[t][x];
    if (x == y) return x;
    for (int t=LOG_V; t--;) {
	if (parent[t][x] != parent[t][y]) {
	    x = parent[t][x];
	    y = parent[t][y];
	}
    }
    return parent[0][x];
}

int distance(int x, int y, int z=-1) { // number of edges
    if (z == -1) z = lca(x, y);
    return depth[x] + depth[y] - 2 * depth[z];
}

int N;
void read_graph(VI G[]) {
    REP (i, N-1) {
	int x, y;
	scanf("%d%d", &x, &y);
	x--; y--;
	G[x].push_back(y);
	G[y].push_back(x);
    }
}

int Lstart, Sstart;
VI L[200011], S[200011];

bool inf[200011];
bool use[200011];
int dist[200011];

void bfs0() {
    memset(use, 0, sizeof use);
    VI ord; ord.reserve(N);
    ord.push_back(Lstart);
    use[Lstart] = true;
    dist[Lstart] = 0;
    for (int i=0; i<(int)ord.size(); i++) {
	int v = ord[i];
	EACH (e, L[v]) if (!use[*e] && depth[*e] > dist[v]+1) {
	    ord.push_back(*e);
	    use[*e] = true;
	    dist[*e] = dist[v] + 1;
	}
    }
}

void MAIN() {
    scanf("%d%d%d", &N, &Lstart, &Sstart);
    Lstart--;
    Sstart--;
    read_graph(L);
    read_graph(S);
    build_lca(S, N, Sstart);

    REP (v, N) {
	EACH (e, L[v]) if (distance(v, *e) >= 3) {
	    inf[v] = true;
	}
    }

    bfs0();

    REP (v, N) {
	if (use[v] && inf[v]) {
	    puts("-1");
	    return;
	}
    }

    int ans = 0;
    REP (v, N) if (use[v]) {
	amax(ans, depth[v]*2);
    }
    printf("%d\n", ans);
}

