int deep[N],fa[N];
int dfn[N],efn[N],tot;
void dfs(int u,int dep){
    deep[u]=dep;
    dfn[u]=++tot;
    for(int i=hB[u];i;i=B[i].nxt){
        int v=B[i].v;
        if(v==fa[u])
            continue;
        fa[v]=u;
        dfs(v,dep+1);
    }
    efn[u]=++tot;
}
bool check(int x,int y){//Islongedge?
    if(dfn[x]>dfn[y])
        swap(x,y);
    if(dfn[x]<=dfn[y]&&efn[y]<=efn[x])
        return deep[y]-deep[x]>2;
    return fa[x]!=fa[y];
}
