struct edge{
	int to,next;
	edge(int _to=0,int _next=0):to(_to),next(_next){}
}e[MAXN<<1],e2[MAXN<<1];

int n,x1,x2;
int g[MAXN],nume;
int g2[MAXN],nume2;
int dep[MAXN],size[MAXN],son[MAXN],top[MAXN],pre[MAXN],w[MAXN],pt[MAXN],numw;
int pre2[MAXN],h[MAXN],dep2[MAXN];
bool flag;
int ans;

void addEdge(int u,int v){
	e[nume]=edge(v,g[u]);
	g[u]=nume++;
}

void addEdge2(int u,int v){
	e2[nume2]=edge(v,g2[u]);
	g2[u]=nume2++;
}

void dfs(int x,int p){
	size[x]=1; son[x]=0;
	pre[x]=p;
	for(int i=g[x];~i;i=e[i].next)
		if(e[i].to^p){
			dep[e[i].to]=dep[x]+1;
			dfs(e[i].to,x);
			size[x]+=size[e[i].to];
			if(size[e[i].to]>size[son[x]])
				son[x]=e[i].to;
		}
}

void dfs2(int x,int p){
	pt[w[x]=++numw]=x;
	if(son[x]){
		top[son[x]]=top[x];
		dfs2(son[x],x);
	}
	for(int i=g[x];~i;i=e[i].next)
		if(e[i].to!=p && e[i].to!=son[x]){
			top[e[i].to]=e[i].to;
			dfs2(e[i].to,x);
		}
}

int getLCA(int x,int y){
	while(top[x]^top[y]){
		if(dep[top[x]]<dep[top[y]]) swap(x,y);
		x=pre[top[x]];
	}
	return dep[x]<dep[y]?x:y;
}

int getDis(int x,int y){
	return dep[x]+dep[y]-2*dep[getLCA(x,y)];
}

int goUp(int x,int d){
	while(dep[x]-dep[top[x]]+1<=d){
		d-=dep[x]-dep[top[x]]+1;
		x=pre[top[x]];
	}
	return pt[w[x]-d];
}

int step(int x,int y){
	int lca=getLCA(x,y);
	if(lca!=x) return pre[x];
	return goUp(y,dep[y]-dep[x]-1);
}

void dfs3(int x,int p){
	pre2[x]=p;
	for(int i=g2[x];~i;i=e2[i].next)
		if(e2[i].to^p){
			int y=e2[i].to;
			int dis=getDis(x,y);
			if(dis>=3 && x!=h[x]) flag=1;
			if(y==h[x] || x==h[x]) continue;
			h[y]=step(h[x],y);
			dep2[y]=dep2[x]+1;
			dfs3(y,x);
		}
	int res=getDis(h[x],x)*2+dep2[x]*2;
	ans=max(ans,res);
}

