using namespace std;
struct edges
{
	int to,next;
}edge[808080];
int firstr[202020],firstb[202020],now;
void adde(int *first,int x,int y)
{
	now++;
	edge[now].to=y;
	edge[now].next=first[x];
	first[x]=now;
}
int dep[202020],fa[202020];
int dis[202020],mi[202020];
bool mk[202020];
void work(int p)
{
	for(int e=firstb[p];e;e=edge[e].next)
		if(edge[e].to!=fa[p])
		{
			dep[edge[e].to]=dep[p]+1;
			fa[edge[e].to]=p;
			work(edge[e].to);
		}
}
void finddis(int p,int fa1)
{
	for(int e=firstr[p];e;e=edge[e].next)
		if(edge[e].to!=fa1)
		{
			dis[edge[e].to]=dis[p]+1;
			mi[edge[e].to]=mi[p];
			if(dep[edge[e].to]<dep[mi[p]])
				mi[edge[e].to]=edge[e].to;
			finddis(edge[e].to,p);
		}
}
int getdis(int x,int y)
{
	int ret=0;
	while(ret<3&&x!=y)
	{
		if(dep[x]>=dep[y])
		{
			x=fa[x];
			ret++;
		}
		else
		{
			y=fa[y];
			ret++;
		}
	}
	return ret;
}
