		adde(firstr,b,a);
	}
	for(int i=1;i<n;i++)
	{
		scanf("%d%d",&a,&b);
		adde(firstb,a,b);
		adde(firstb,b,a);
	}
	dep[y]=fa[y]=0;
	work(y);
	dis[x]=0;
	memset(mi,0,sizeof(mi));
	finddis(x,0);
	for(int i=1;i<=n;i++)
		for(int e=firstr[i];e;e=edge[e].next)
