bool saved[N];
int ans = 0;

void dfs0(int a, int from) {
    for (auto b : g[a]) {
        if (b == from) continue;
        dist[b] = dist[a] + 1;
        pred[0][b] = a;
        dfs0(b, a);
    }
}

int getDistance(int a, int b) {
    //cout << a << "/" << b << endl;
    int res = dist[a] + dist[b];
    if (dist[a] < dist[b]) swap(a, b);
    int log = LOG - 1;
    while (log >= 0) {
        if (pred[log][a] == -1 || dist[pred[log][a]] < dist[b]) {
            log--;
        } else {
            a = pred[log][a];
        }
    }
    //cout << "-> " << a << " " << b << endl;
    if (a == b) {
        res -= 2 * dist[a];
        return res;
    }
    log = LOG - 1;
    while (log >= 0) {
        assert(a != b);
        if (pred[log][a] == pred[log][b]) {
            log--;
        } else {
            a = pred[log][a];
            b = pred[log][b];
        }
    }
    res -= 2 * dist[pred[0][a]];
    return res;
}

void dfs1(int a, int from) {
    for (auto b : f[a]) {
        if (b == from) continue;
        dist2[b] = dist2[a] + 1;
        dfs1(b, a);
    }
}

void dfs2(int a, int from, int t) {
    if (dist[a] <= t) return;
    if (saved[a]) {
        cout << -1 << endl;
        exit(0);
    }
    ans = max(ans, dist[a]);
    for (auto b : f[a]) {
        if (b == from) continue;
        dfs2(b, a, t + 1);
    }
}

