    debug(x,depb[x]);
    ancb[0][x] = p;
    for (int i=1;1<<i < MAXn;++i) {
        ancb[i][x] = ancb[i-1][ancb[i-1][x]];
    }
    for (auto i:b[x]) {
        if (i == p) continue;
        dfsb(i,x);
    }
}
int LCA(int x,int y){
    if (depb[x] < depb[y]) {
        swap(x,y);
    }
    for (int i=MAXlg-1;i>=0;--i) {
        if (((depb[x] - depb[y])>>i)&1) {
            x = ancb[i][x];
        }
    }
    if (x == y) return x;
    for (int i=MAXlg-1;i>=0;--i) {
        if (ancb[i][x] != ancb[i][y]) {
            x = ancb[i][x];
            y = ancb[i][y];
        }
    }
    return ancb[0][x];
}
int dis(int x,int y){
    int lca = LCA(x,y);
    debug(x,y,lca);
