    ::fa[u]=fa;
    dep[u]=dep[fa]+1;
    dfn[u]=++ncnt;
    for(int i=Ghead[u];i;i=G[i].nex){
        int v=G[i].v;
        if(v==fa) continue;
        dfs(v,u);
    }
    efn[u]=++ncnt;
}

