	to[++e] = v, nxt[e] = st[u];
	st[u] = e;
}

int E, St[MAXN], To[MAXN<<1];
int Nxt[MAXN<<1];
inline void Add(int u, int v) {
	To[++E] = v, Nxt[E] = St[u];
	St[u] = E;
}

int n, dep[MAXN], rx, ry;
int fa[MAXN][20], ans;
bool vis[MAXN], flag;

void dfs(int u) {
	for(int i = st[u]; i; i = nxt[i]) {
		int v = to[i];
		if(v == fa[u][0]) continue;
		dep[v] = dep[u]+1;
		fa[v][0] = u, dfs(v);
	}
}

inline int LCA(int u, int v) {
	if(dep[u] < dep[v]) swap(u, v);
	for(int i = 18; i >= 0; i--) 
		if(dep[fa[u][i]] >= dep[v]) 
			u = fa[u][i];
	if(v == u) return u;
	for(int i = 18; i >= 0; i--) 
		if(fa[u][i] ^ fa[v][i])
			u = fa[u][i], v = fa[v][i];
	return fa[u][0];
}

void Dfs(int u, int d) {
	vis[u] = true;
	//printf("%d %d\n", u, dep[u]);
	ans = max(ans, (dep[u]-1)<<1);
	for(int i = St[u]; i; i = Nxt[i]) {
		int v = To[i];
		if(vis[v]) continue;
		if(dep[v] <= d+1) continue;
		int lca = LCA(u, v);
		if(dep[u]+dep[v]-(dep[lca]<<1) > 2) flag = true;
		Dfs(v, d+1);
	}
}

