const int N = 2e5 + 10;
const int LOG = 20;

struct Tree {
    vvi g;
    vi parent;
    int timer = 1;
    vi tin, tout;
    vi dip;

    vvi dp;

    Tree(int n) {
        g.resize(n + 1);
        parent.resize(n + 1, -1);
        tin.resize(n + 1);
        tout.resize(n + 1);
        dip.resize(n + 1, 0);
        dp.resize(n + 1, vi(LOG));
    }

    void add_edge(int v, int u) {
        g[u].push_back(v);
        g[v].push_back(u);
    }

    void dfs(int v, int par = -1) {
        tin[v] = timer++;
        if (par == -1)
            par = v;
        parent[v] = par;
        dp[v][0] = par;
        for (int i = 1; i < LOG; ++i) {
            dp[v][i] = dp[dp[v][i - 1]][i - 1];
        }
        for (int to : g[v]) {
            if (to == par)
                continue;
            dip[to] = dip[v] + 1;
            dfs(to, v);
        }
        tout[v] = timer++;
    }

    int dist(int u, int v) {
        if (!is_par(u, v))
            swap(u, v);
        return dip[v] - dip[u];
    }

    int jump(int v, int dst) {
        if (!dst)
            return v;
        for (int i = LOG - 1; i >= 0; --i) {
            if ((1 << i) < dst) {
                v = dp[v][i];
                dst -= 1 << i;
            }
        }
        return dp[v][0];
    }

    bool is_par(int u, int v) {
        return tin[u] <= tin[v] && tin[v] <= tout[u];
    }
};

int n, x, y;

Tree input_tree(int n, int root) {
    Tree ret(n);
    fori (_, n - 1) {
        int u, v;
        cin >> u >> v;
        ret.add_edge(u, v);
    }
    ret.dfs(root);
    return ret;
}

const int oo = 1e9 + 10;
int dp[N];
Tree red(N), blue(N);

int find_to(int root, int to) {
    if (!blue.is_par(root, to))
        return oo;
    return blue.jump(to, blue.dist(root, to) - 1);
}

void dfs(int v) {
//    cerr << "dp[" << v << "] = " << dp[v] << endl;
    for (int to : red.g[v]) {
        if (to == red.parent[v])
            continue;
        if (blue.parent[to] == dp[v] ||
            blue.parent[dp[v]] == to ||
            to == dp[v]) {
            continue;
        }
        dp[to] = find_to(dp[v], to);
        if (dp[to] != oo) {
            dfs(to);
        }
    }
}

void smain() {
    cin >> n >> x >> y;
    red = input_tree(n, x);
    blue = input_tree(n, y);
    fill(dp, dp + n + 1, oo);
    dp[x] = y;
    dfs(x);

    for (int v = 1; v <= n; ++v) {
        if (dp[v] == oo)
            continue;
        int subt = blue.parent[v];
        for (int to : red.g[v]) {
            if (blue.is_par(subt, to) ||
                to == subt ||
                blue.parent[to] == subt ||
                blue.parent[subt] == to)
                continue;
            cout << "-1\n";
            exit(0);
        }
    }

    int ans = 0;
    for (int v = 1; v <= n; ++v) {
        if (dp[v] != oo) {
            inmax(ans, blue.dip[v]);
        }
    }
    cout << ans * 2;

}

