{
    fa[u]=u;deep[u]=deep[f]+1;
    for(int i=h[u];i+1;i=e[i].next)
    {
        int v=e[i].v;
        if(v==f) continue;
        dfs(v,u);
    }
    for(int i=0;i<g[u].size();i++)
    {
        int v=g[u][i];
        if(vis[v]){
            int t=findfa(v);
