    int head,tail;
    head=tail=0;
    q[tail++]=x;
    int ans=2*deep[x];
    while(head<tail)
    {
        int u=q[head++];
        if(check[u]) {
            //printf("%dss %d %d\n",u,head,tail);
            return -1;
