        for(int i=0;i<g[u].size();i++)
        {
            int v=g[u][i];
            if(dis[v]>dis[u]+1){
                if(deep[v]>dis[u]+1){
                    dis[v]=dis[u]+1;
                    q[tail++]=v;
                    ans=max(ans,2*deep[v]);
                }
            }
        }
    }
    return ans;
}
