const int MAXN = 2e5 + 5, LOGN = 19;

int N, X, Y;
vector<int> GX[MAXN], GY[MAXN];

void input()
{
    read(N); read(X); read(Y);
    for (int i = 1; i < N; ++i) {
        int u, v;
        read(u); read(v);
        GX[u].push_back(v);
        GX[v].push_back(u);
    }
    for (int i = 1; i < N; ++i) {
        int u, v;
        read(u); read(v);
        GY[u].push_back(v);
        GY[v].push_back(u);
    }
}

int dep[MAXN], jmp[MAXN][LOGN];
int ans;

void dfsY(int u)
{
    dep[u] = dep[jmp[u][0]] + 1;
    for (int i = 0; i < SZ(GY[u]); ++i) {
        int v = GY[u][i];
        if (v == jmp[u][0])
            continue;
        jmp[v][0] = u;
        for (int j = 1; j < LOGN; ++j) {
            jmp[v][j] = jmp[jmp[v][j - 1]][j - 1];
        }
        dfsY(v);
    }
}

int getnextY(int x, int y)
{
    assert(x != y);
    for (int i = LOGN - 1; i >= 0; --i) {
        if (dep[jmp[x][i]] > dep[y])
            x = jmp[x][i];
    }
    return jmp[x][0] == y ? x : jmp[y][0];
}

int getlcaY(int u, int v)
{
    if (dep[u] < dep[v])
        std::swap(u, v);
    for (int i = LOGN - 1; i >= 0; --i) {
        if (dep[jmp[u][i]] >= dep[v])
            u = jmp[u][i];
    }
    if (u == v)
        return u;
    for (int i = LOGN - 1; i >= 0; --i) {
        if (jmp[u][i] != jmp[v][i]) {
            u = jmp[u][i];
            v = jmp[v][i];
        }
    }
    return jmp[u][0];
}

int getdisY(int u, int v)
{
    return dep[u] + dep[v] - dep[getlcaY(u, v)] * 2;
}

void dfsX(int x, int y, int fa, int cur)
{
    chkmax(ans, cur + getdisY(x, y) * 2);
    if (x == y)
        return;
    for (int i = 0; i < SZ(GX[x]); ++i) {
        int v = GX[x][i];
        if (v == fa)
            continue;
        if (v == y) {
            chkmax(ans, cur + 1);
            continue;
        }
        if (getdisY(x, v) > 2) {
            puts("-1");
            exit(0);
        }
        dfsX(v, getnextY(v, y), x, cur + 2);
    }
}

void solve()
{
    dfsY(Y);
    dfsX(X, Y, 0, 0);
    printf("%d\n", ans);
}

