const int MAX_V = 201000;
class LCA {
public : 
int parent[20][MAX_V];
vector<int>graph[MAX_V];
int depth[MAX_V];
int root;//

void add_LCA(int u,int v) {
	graph[u].push_back(v);
	graph[v].push_back(u);
}


void dfsLCA(int v,int p,int d) {
	parent[0][v] = p;
	depth[v]=d;
	for(int i=0; i<graph[v].size(); i++) {
		if(graph[v][i]!=p) dfsLCA(graph[v][i],v,d+1) ;
	}

}

void initLCA(int V) {
	dfsLCA(root,-1,0);

	for(int k=0; k+1<20; k++) {
		for(int v=0; v<V; v++) {
			if(parent[k][v]<0) parent[k+1][v]=-1;
			else parent[k+1][v] = parent[k][parent[k][v]];
		}
	}

}

int solveLCA(int u,int v) {
	int ans = 0;
	if(depth[u]>depth[v]) swap(u,v) ;
	for(int k=0; k<20; k++) {
		if((depth[v]-depth[u])>>k&1) {
			v = parent[k][v];
			ans += (1<<k);
		}
	}
	if(u==v) {
		return ans;
	//	return u;
	}
	for(int k=20-1; k>=0; k--) {
		if(parent[k][u]!=parent[k][v]) {
			u = parent[k][u];
			v = parent[k][v];
			ans += (1<<k) * 2;
		}
	}
	return ans + 2;
//	return parent[0][u];
}

};

