	int v, nxt;
	E(int _v=0, int _nxt=0) : v(_v), nxt(_nxt) {}
};
struct Graph {
	int head[NN], totE;
	E edge[NN*2];
	void AddEdge(int u, int v) {
		edge[++totE] = E(v, head[u]);
		head[u] = totE;
	}
} a, b;
int N, X, Y, da[NN], db[NN], fab[NN];
bool save[NN];
void Dfs1(int u, int fr) {
	fab[u] = fr;
	db[u] = db[fr] + 1;
	for (int p = b.head[u]; p; p = b.edge[p].nxt) {
		int v = b.edge[p].v; // 
		if (v != fr)
			Dfs1(v, u);
	}
}
bool inf, edgetag[NN];
void Dfs2(int u, int fr, int is) {
	da[u] = da[fr] + 1;
	if (da[u] >= db[u]) is = 0;
	else if (is) save[u] = 1;
	if (fr != 0) {
		int x = u, y = fr;
		if (db[x] < db[y]) swap(x, y);
		if (fab[x] == fab[y] || fab[x] == y || fab[fab[x]] == y) ;
		else {
			inf = 1;
			edgetag[u] = 1;
		}
	}
	for (int p = a.head[u]; p; p = a.edge[p].nxt) {
		int v = a.edge[p].v;
		if (v != fr) {
			Dfs2(v, u, is);
			if (edgetag[v] && (save[u] || save[v]))
				inf = 1;
		}
	}
}
