		int u, v;
		scanf("%d%d", &u, &v);
		b.AddEdge(u, v);
		b.AddEdge(v, u);
	}
	Dfs1(Y, 0);
	Dfs2(X, 0, 1);
	if (inf) printf("-1\n");
	else {
		int ans = 0;
		for (int i = 1; i <= N; ++i)
			if (save[i])
				ans = max(ans, db[i]-1);
		ans *= 2;
		printf("%d\n", ans);
	}
	return 0;
}

