using namespace std;

template <class Type>
class LCA {
    public:
    LCA(int n);
    ~LCA();
    void add_edge(int from, int to, Type cost = 1);
    void add_undirected_edge(int from, int to, Type cost = 1);
    void init(int root = 0);
    int up(int x, int c);
    int lca(int x, int y);
    Type dist(int x, int y);

    private:
    struct edge {
        int to;
        Type cost;
        edge(int to, Type cost) : to(to), cost(cost) {}
    };
    vector <vector <edge> > graph;
    int size;
    int high;
    int *parent;
    int *depth;
    Type *distance;
    int dfs(int now, int par, int dep, Type dist);
};

template <class Type> LCA<Type>::LCA(int n) : size(n), high(32 - __builtin_clz(n)), graph(n) {
    parent = (int *)malloc(sizeof(int) * size * high);
    depth = (int *)malloc(sizeof(int) * size);
    distance = (Type *)malloc(sizeof(Type) * size);
}

template <class Type> LCA<Type>::~LCA() {
    free(parent);
    free(depth);
    free(distance);
}

template <class Type> void LCA<Type>::add_edge(int from, int to, Type cost) {
    graph[from].push_back(edge(to, cost));
}

template <class Type> void LCA<Type>::add_undirected_edge(int from, int to, Type cost) {
    graph[from].push_back(edge(to, cost));
    graph[to].push_back(edge(from, cost));
}

template <class Type> void LCA<Type>::init(int root) {
    dfs(root, -1, 0, 0);
    for (int i = 0; i + 1 < high; i++) {
        for (int j = 0; j < size; j++) {
            if (parent[j * high + i] == -1) {
                parent[j * high + i + 1] = -1;
            } else {
                parent[j * high + i + 1] = parent[parent[j * high + i] * high + i];
            }
        }
    }
}

template <class Type> int LCA<Type>::up(int x, int c) {
    while (c > 0) {
        int right = __builtin_ctz(c);
        x = parent[x * high + right];
        c ^= 1 << right;
    }
    return x;
}

template <class Type> int LCA<Type>::lca(int x, int y) {
    if (depth[x] > depth[y]) swap(x, y);
    y = up(y, depth[y] - depth[x]);
    if (x == y) return x;
    for (int i = 31 - __builtin_clz(depth[x]); i >= 0; i--) {
        if (parent[x * high + i] != parent[y * high + i]) {
            x = parent[x * high + i];
            y = parent[y * high + i];
        }
    }
    return parent[x * high];
}

template <class Type> Type LCA<Type>::dist(int x, int y) {
    return distance[x] + distance[y] - distance[lca(x, y)] * 2;
}

template <class Type> int LCA<Type>::dfs(int now, int par, int dep, Type dist) {
    int val = 1e9;
    for (int i = 0; i < graph[now].size(); i++) {
        if (graph[now][i].to != par) val = min(val, dfs(graph[now][i].to, now, dep + 1, dist + graph[now][i].cost) + 1);
    }
    parent[now * high] = par;
    depth[now] = dep;
    distance[now] = dist;
    return val;
}

