ll tim = 0;
void dfs_lca(ll x, ll par) {
    st[x] = tim++;
    pars[0][x] = par;
    fori (i, maxnlg - 1) pars[i + 1][x] = pars[i][pars[i][x]];
    for (auto y : g[1][x]) {
        if (y != par) {
            ht[y] = ht[x] + 1;
            dfs_lca(y, x);
        }
    }
    ft[x] = tim;
}

ll LCA(ll a, ll b) {
    if (st[a] > st[b]) swap(a, b);
    if (ft[a] >= ft[b]) return a;
    forir (i, maxnlg) {
        if (ft[pars[i][a]] < ft[b]) a = pars[i][a];
    }
    return pars[0][a];
}

