vector<int> R[MX], B[MX];

int dR[MX], dB[MX], P[MX][20];

int dfsB(int x, int p = -1){
	for(int c : B[x]){
		if( c == p ) continue;
		dB[c] = dB[x] + 1;
		dfsB(c, x);
	}
}

int dfs(int x, int p = -1){
	for(int c : R[x]){
		if( c == p ) continue;
		dR[c] = dR[x] + 1;
		P[c][0] = x;
		for(int t = 1; t < 20; t++) P[c][t] = P[P[c][t-1]][t-1];
		dfs(c, x);
	}
}

int dist(int a, int b){
	int ans = 0;
	if( dR[a] < dR[b] ) swap(a, b);
	for(int i = 0; i < 20; i++){
		if( (dR[a] - dR[b])&1<<i ) ans += 1<<i, a = P[a][i];
	}
	for(int i = 19; i >= 0; i--){
		if( P[a][i] != P[b][i] ) ans += 1<<i+1, a = P[a][i], b = P[b][i];
	}
	return ans + (a==b ? 0 : 2);
}

int win[MX];
int ans = 0;

void solve(int x, int p = -1){
	ans = max(ans, dR[x] * 2);
	if( win[x] ) ans = 1e9;
	for(int c : B[x]){
		if( c == p ) continue;
		if( dB[c] >= dR[c] ){
			ans = max(ans, dR[c] * 2);
			continue;
		}
		solve(c, x);
	}
}

