int lca(int v, int u) {
   if (distb[v] > distb[u]) swap(v, u);
   for (int z = 17; z >= 0; --z) if (distb[anc[z][u]] >= distb[v]) u = anc[z][u];
   if (v == u) return v;
   for (int z = 17; z >= 0; --z) if (anc[z][v] != anc[z][u]) {
      v = anc[z][v];
      u = anc[z][u];
   }
   return anc[0][v];
}

int get_dist(int v, int u) {
   return distb[v] + distb[u] - 2 * distb[lca(v, u)];
}

