#include <bits/stdc++.h>

using namespace std;

const int NMAX = 200000 + 5;

int N;
vector <int> tomTree[NMAX];
vector <int> jerryTree[NMAX];

int tomCnt;
int tomH[NMAX];
int tomFathers[19][NMAX];
int tomFirst[NMAX];
int tomLast[NMAX];

void dfsPrepareTomTree(int node) {
    tomFirst[node] = ++tomCnt;
    if (tomFathers[0][node])
        tomTree[node].erase(find(tomTree[node].begin(), tomTree[node].end(), tomFathers[0][node]));

    for (int i = 1; i < 19; ++ i)
        tomFathers[i][node] = tomFathers[i - 1][tomFathers[i - 1][node]];

    for (auto it: tomTree[node]) {
        tomH[it] = 1 + tomH[node];
        tomFathers[0][it] = node;
        dfsPrepareTomTree(it);  
    }
    tomLast[node] = tomCnt;
}

int moveCloser(int t, int j) {
    if (t == j) // No need to move
        return t;
    else if (tomFirst[t] <= tomFirst[j] && tomFirst[j] <= tomLast[t]) { // Move in a son
        int st = 1, dr = tomTree[t].size() - 1;
        int ans = 0;
        while (st <= dr) {
            int mid = (st + dr) >> 1;
            if (tomFirst[tomTree[t][mid]] <= tomFirst[j])
                ans = mid, st = mid + 1;
            else
                dr = mid - 1;
        }
        return tomTree[t][ans];
    }
    else // Move in the father
        return tomFathers[0][t];
}

inline int lca(int a, int b) {
    if (tomH[a] > tomH[b])
        swap(a, b);
    for (int i = 18; i >= 0; -- i)
        if ((1 << i) <= tomH[b] - tomH[a])
            b = tomFathers[i][b];
    if (a == b)
        return a; 
    for (int i = 18; i >= 0; -- i)
        if (tomFathers[i][a] != tomFathers[i][b])
            a = tomFathers[i][a], b = tomFathers[i][b];
    return tomFathers[0][a];
}

inline int getDist(int a, int b) {
    return tomH[a] + tomH[b] - 2 * tomH[lca(a, b)];
}

const int INF = 2E9 + 256;
bool easyWinJerry[NMAX];

bool jerryVis[NMAX];
int play(int t, int j) { // Jerry moves
    if (t == j)
        return 0;
    if (easyWinJerry[j])
        return INF;
    jerryVis[j] = true;

    int longest = 2 * getDist(t, j);
    for (auto it: jerryTree[j])
        if (!jerryVis[it])
            longest = max(longest, 2 + play(moveCloser(t, it), it));
    jerryVis[j] = false;
    return longest;
}

