    }
    dfsPrepareTomTree(t);

    for (int i = 1; i <= N; ++ i)
        for (auto it: jerryTree[i]) {
            int d = getDist(i, it);
            if (d >= 3)
                easyWinJerry[i] = easyWinJerry[it] = true;
        }

    int ans = play(t, j);
    if (ans >= INF)
        cout << "-1\n";
    else
        cout << ans << '\n';
    return 0;
}

