using namespace std;

const int N = 200200;
const int LOG = 18;
int n;
vector<int> g[N], g2[N];
int X, Y;
int h[N];
int dist[N];
int par[N][LOG];
int q[N];
int topQ;

void read()
{
	scanf("%d%d%d", &n, &X, &Y);
	X--;Y--;
	for (int i = 1; i < n; i++)
	{
		int v, u;
		scanf("%d%d", &v, &u);
		v--;u--;
		g[v].push_back(u);
		g[u].push_back(v);
	}
	for (int i = 1; i < n; i++)
	{
		int v, u;
		scanf("%d%d", &v, &u);
		v--;u--;
		g2[v].push_back(u);
		g2[u].push_back(v);
	}
	return;
}

void dfs(int v)
{
	for (int u : g2[v])
	{
		if (h[u] != -1) continue;
		h[u] = h[v] + 1;
		par[u][0] = v;
		for (int k = 1; k < LOG; k++)
		{
			int w = par[u][k - 1];
			if (w == -1)
			{
				par[u][k] = -1;
				continue;
			}
			par[u][k] = par[w][k - 1];
		}
		dfs(u);
	}
	return;
}

int up(int v, int dh)
{
	for (int k = LOG - 1; k >= 0; k--)
	{
		if (dh >= (1 << k))
		{
			dh -= 1 << k;
			v = par[v][k];
		}
	}
	return v;
}
int LCA(int v, int u)
{
	if (h[v] > h[u]) swap(v, u);
	u = up(u, h[u] - h[v]);
	if (v == u) return v;
	for (int k = LOG - 1; k >= 0; k--)
	{
		if (par[v][k] != par[u][k])
		{
			v = par[v][k];
			u = par[u][k];
		}
	}
	return par[v][0];
}

void precalc()
{
	for (int i = 0; i < n; i++)
	{
		h[i] = -1;
		for (int j = 0; j < LOG; j++)
			par[i][j] = -1;
	}
	h[Y] = 0;
	dfs(Y);
	return;
}

void solve()
{
	for (int i = 0; i < n; i++)
		dist[i] = N;
	topQ = 1;
	q[0] = X;
	dist[X] = 0;
	int ans = 0;
	for (int k = 0; k < topQ; k++)
	{
		int v = q[k];
		ans = max(ans, 2 * h[v]);
		for (int u : g[v])
		{
			int w = LCA(v, u);
			if (h[v] + h[u] - 2 * h[w] >= 3)
			{
				printf("-1\n");
				return;
			}
			if (dist[u] <= dist[v] + 1) continue;
			if (h[u] < dist[v] + 1) continue;
			if (h[u] == dist[v] + 1)
			{
				ans = max(ans, 2 * h[u]);
				continue;
			}
			dist[u] = dist[v] + 1;
			q[topQ++] = u;
		}
	}
	printf("%d\n", ans);
	return;
}

int main()
{
//	freopen("input.txt", "r", stdin);
//	freopen("output.txt", "w", stdout);

	read();
	precalc();
	solve();

	return 0;
}

