#include <bits/stdc++.h>
#include <codecvt>

const int N = 200001;

int n, x, y, a[N], b[N], depth[N], parent[N], escape[N], dist[N];
std::vector<int> tree[N], graph[N];

void prepare(int p, int u) {
  depth[u] = depth[p] + 1, parent[u] = p;
  for (int v : tree[u]) {
    if (v != p) {
      prepare(u, v);
    }
  }
}

int treedist(int a, int b) {
  int d = 0;
  while (d < 3 && a != b) {
    int &u = depth[a] > depth[b] ? a : b;
    d++, u = parent[u];
  }
  return d;
}

int main() {
  scanf("%d%d%d", &n, &x, &y);
  for (int i = 0; i < n - 1; ++i) {
    scanf("%d%d", a + i, b + i);
  }
  for (int i = 0, c, d; i < n - 1; ++i) {
    scanf("%d%d", &c, &d);
    tree[c].push_back(d);
    tree[d].push_back(c);
  }
  depth[0] = -1;
  prepare(0, y);
  for (int i = 0; i < n - 1; ++i) {
    if (treedist(a[i], b[i]) == 3) {
      escape[a[i]] = escape[b[i]] = true;
    } else {
      graph[a[i]].push_back(b[i]);
      graph[b[i]].push_back(a[i]);
    }
  }
  std::vector<int> queue;
  queue.push_back(x);
  memset(dist, -1, sizeof(dist));
  dist[x] = 0;
  int result = 0;
  for (int head = 0; head < static_cast<int>(queue.size()); ++head) {
    int u = queue[head];
    if (dist[u] >= depth[u]) {
      continue;
    }
    if (escape[u]) {
      result = -1;
      break;
    }
    result = std::max(result, depth[u] << 1);
    for (int v : graph[u]) {
      if (dist[v] == -1) {
        dist[v] = dist[u] + 1;
        queue.push_back(v);
      }
    }
  }
  printf("%d\n", result);
}

