#include <bits/stdc++.h>
using namespace std;

struct Graph
{
    int N;
    vector<vector<int>> red_neighbors, blue_neighbors;

    Graph(const int N) : N(N), red_neighbors(N), blue_neighbors(N) {}

    void add_red_edge(const int a, const int b)
    {
        red_neighbors[a].push_back(b);
        red_neighbors[b].push_back(a);
    }

    void add_blue_edge(const int a, const int b)
    {
        blue_neighbors[a].push_back(b);
        blue_neighbors[b].push_back(a);
    }

    int solve(const int X, const int Y) const
    {
        auto blue_depths = vector<int>(N);
        auto blue_parents = vector<int>(N);
        const function<void(int, int)> blue_dfs = [&](const int v, const int parent)
        {
            for (const auto w : blue_neighbors[v]) if (w != parent)
            {
                blue_depths[w] = blue_depths[v]+1;
                blue_parents[w] = v;
                blue_dfs(w, v);
            }
        };
        blue_parents[Y] = Y;
        blue_dfs(Y, -1);

        const auto blue_distance_1 = [&](const int v, const int w)
        {
            return blue_parents[v] == w || blue_parents[w] == v;
        };

        const auto blue_distance_2 = [&](const int v, const int w)
        {
            return blue_parents[blue_parents[v]] == w ||
                   blue_parents[blue_parents[w]] == v ||
                   blue_parents[v] == blue_parents[w];
        };

        auto red_distances = vector<int>(N, -1);
        auto win = false;
        const function<void(int, int)> red_dfs = [&](const int v, const int parent)
        {
            if (red_distances[v] >= blue_depths[v]) return;

            for (const auto w : red_neighbors[v]) if (w != parent)
            {
                if (!(blue_distance_1(v, w) || blue_distance_2(v, w)))
                    win = true;
                else
                {
                    red_distances[w] = red_distances[v]+1;
                    red_dfs(w, v);
                }
            }
        };
        red_distances[X] = 0;
        red_dfs(X, -1);

        if (win) return -1;
        else
        {
            auto result = 0;
            for (auto v = 0; v < N; v++)
                if (red_distances[v] != -1)
                    result = max(result, 2*blue_depths[v]);
            return result;
        }
    }
};

int main()
{
    int N, X, Y; cin >> N >> X >> Y;
    X--; Y--;

    auto graph = Graph(N);

    for (auto i = 0; i < N-1; i++)
    {
        int a, b; cin >> a >> b;
        a--; b--;
        graph.add_red_edge(a, b);
    }

    for (auto i = 0; i < N-1; i++)
    {
        int a, b; cin >> a >> b;
        a--; b--;
        graph.add_blue_edge(a, b);
    }

    cout << graph.solve(X, Y) << endl;
}

